package org.example;
import akka.actor.ActorRef;
import akka.actor.ActorSystem;

public class App {
    public static final int N = 5;
    public static final int W = 3;
    public static final int R = 3;
    public static final int T = 9999;

    public static void main(String[] args) {
        ActorSystem sistema = ActorSystem.create("sistema");

        ActorRef pino = sistema.actorOf(Attore.props("Pino", "Ciaone"));
        ActorRef daniele = sistema.actorOf(Attore.props("Daniele", "Ciaissimo"));

        pino.tell(new BeaconMessage(), daniele);
        daniele.tell(new BeaconMessage(), pino);

        pino.tell(new StartMessage(), ActorRef.noSender());
        daniele.tell(new StartMessage(), ActorRef.noSender());

        try {
            System.out.println(">> Press Enter to End <<");
            System.in.read();
        }catch (Exception e) {}
        sistema.terminate();
    }
}
